import { useState } from 'react'
import "./index.css"
import TopButton from "./components/TopButton"
import getData from './components/getData'
import { GROUP } from '../../data'

function Home() {
    const [selectGroup, setSelectGroup] = useState();

    function groupSelect(selectedButton) {
        setSelectGroup(selectedButton);
        console.log(selectGroup);
    }

    return (
        <div className="app">
            <h1>代購專用</h1>
            <menu className="menu">
                <TopButton
                    isSelected={selectGroup === 'ITZY'} 
                    onSelect={() => groupSelect('ITZY')}> 
                    ITZY 
                </TopButton>
                <TopButton> (G)I-DLE </TopButton>
                <TopButton> LE SSERAFIM </TopButton>
            </menu>
            <section>
                <getData data={GROUP} selectGroup={selectGroup}></getData>
            </section>
        </div>
    )
}

export default Home
