import { useState } from 'react'
import "./index.css"
import TopButton from "./components/TopButton"
import { GROUP } from '../../data'

function Home() {
    const [selectGroup, setSelectGroup] = useState();
    function groupSelect(selectedButton) {
        tabContent = selectedButton;
        setSelectGroup(selectedButton);
        console.log(selectGroup);
    }

    let tabContent = <p>Please select a topic.</p>;
    if(selectGroup){
        tabContent = (
        <div id='tab-content'>
            <h3>{GROUP[selectGroup].id}</h3>
            <p>{GROUP[selectGroup].image}</p>
            <pre>
            <code>
                {GROUP[selectGroup].description}
            </code>
            </pre>
        </div> 
        )
    }
    return (
        <div className="app">
            <h1>代購專用</h1>
            <menu className="menu">
                <TopButton
                    isSelected={selectGroup === 'ITZY'} 
                    onSelect={() => groupSelect('ITZY')}> 
                    ITZY 
                </TopButton>
                <TopButton> (G)I-DLE </TopButton>
                <TopButton> LE SSERAFIM </TopButton>
            </menu>
            <section>
                <getData data={GROUP} selectGroup={selectGroup} />
            </section>
        </div>
    )
}

export default Home
