// Home.js

import { useSelector, useDispatch } from 'react-redux';
import { setGroup } from '../../store/groupSlice';
import TopButton from "./components/TopButton";
import { GROUP } from '../../data';
import "./index.css";

function Home() {
  const selectedGroup = useSelector(state => state.group);
  console.log(selectedGroup);
  const dispatch = useDispatch();

  function groupSelect(selectedButton) {
    dispatch(setGroup(selectedButton)); // 使用Redux動作來設定選定的group
  }

  let tabContent = <p>Please select a topic.</p>;
  if (selectedGroup) {
    tabContent = (
      <div id='tab-content'>
        <h2>{GROUP[selectedGroup].title}</h2>
        <p>{GROUP[selectedGroup].description}</p>
        <img src={GROUP[selectedGroup].image} alt={GROUP[selectedGroup].title} className='photo' />
      </div> 
    )
  }

  return (
    <div className="app">
      <nav className='nav'> 
        <menu className="menu">
          <TopButton
            isSelected={selectedGroup === 'ITZY'} 
            onSelect={() => groupSelect('ITZY')}>
            ITZY 
          </TopButton>
          <TopButton 
            isSelected={selectedGroup === 'GIDLE'} 
            onSelect={() => groupSelect('GIDLE')}>
            (G)I-DLE 
          </TopButton>
          <TopButton
            isSelected={selectedGroup === 'LESSERAFIM'} 
            onSelect={() => groupSelect('LESSERAFIM')}>
            LE SSERAFIM 
          </TopButton>
        </menu>
      </nav>
      <section>
        {tabContent}      
      </section>
    </div>
  );
}

export default Home;
