import React, { useState , useRef } from 'react';
import { useDispatch } from 'react-redux';
import { addProduct } from '../../../store/shopSlice'; // 假設你有一個名為 addProduct 的 Redux 動作

const SetProduct = () => {
  const dispatch = useDispatch();
  const productNameRef = useRef();
  const photoRef = useRef();
  const descriptionRef = useRef();


  const handleImageChange = (e) => {
    // 從事件中獲取選擇的圖片文件
    const selectedImage = e.target.files[0];
    setPhoto(selectedImage);
  };

  const handleAddProduct = () => {
    const productNameValue = productNameRef.current.value;
    const photoValue = photoRef.e.target.files[0];
    const descriptionValue = descriptionRef.current.value;

    // 執行 Redux 動作以新增商品，包括圖片文件
    dispatch(addProduct({ 
        productName: productNameValue, 
        photo: photoValue, 
        description: descriptionValue, 
    }));

    // 清空輸入欄位
    setProductName('');
    setPhoto(null);
    setDescription('');
  };

  return (
    <div>
      <p>代購商品名稱:</p>
      <input type="text" value={productName} onChange={(e) => setProductName(e.target.value)} />
      <p>代購商品照片:</p>
      <input type="file" onChange={handleImageChange} />
      <p>代購商品簡介:</p>
      <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
      <button className="add2" onClick={handleAddProduct}>
        新增
      </button>
    </div>
  );
};

export default SetProduct;
