import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addProduct } from '../../../store/shopSlice'; // 假設你有一個名為 addProduct 的 Redux 動作

const SetProduct = () => {
  const [productName, setProductName] = useState('');
  const [photo, setPhoto] = useState('');
  const [description, setDescription] = useState('');
  const dispatch = useDispatch();

  const handleAddProduct = () => {
    // 執行 Redux 動作以新增商品
    dispatch(addProduct({ productName, photo, description }));

    // 清空輸入欄位
    setProductName('');
    setPhoto('');
    setDescription('');
  };

  return (
    <div>
      <p>商品名稱:</p>
      <input type="text" value={productName} onChange={(e) => setProductName(e.target.value)} />
      <p>商品照片:</p>
      <input type="file" value={photo} onChange={(e) => setPhoto(e.target.value)} />
      <p>商品簡介:</p>
      <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
      <button className="add" onClick={handleAddProduct}>
        新增
      </button>
    </div>
  );
};

export default SetProduct;
