import { useState } from 'react'
import "./index.css"
import TopButton from "./components/TopButton"
import { GROUP } from '../../data'

function Home() {
    const [selectGroup, setSelectGroup] = useState();
    function groupSelect(selectedButton) {
        tabContent = selectedButton;
        setSelectGroup(selectedButton);
        console.log(selectGroup);
    }

    let tabContent = <p>Please select a topic.</p>;
    if(selectGroup){
        tabContent = (
            <div id='tab-content'>
                <h2>{GROUP[selectGroup].title}</h2>
                <img src={GROUP[selectGroup].image} alt={GROUP[selectGroup].title} />
                <p>{GROUP[selectGroup].description}</p>
            </div> 
        )
    }
    return (
        <div className="app">
            <h1>代購專用</h1>
            <menu className="menu">
                <TopButton
                    isSelected={selectGroup === 'ITZY'} 
                    onSelect={() => groupSelect('ITZY')}> 
                    ITZY 
                </TopButton>
                <TopButton 
                    isSelected={selectGroup === 'GIDLE'} 
                    onSelect={() => groupSelect('GIDLE')}> 
                    (G)I-DLE 
                </TopButton>
                <TopButton
                    isSelected={selectGroup === 'LESSERAFIM'} 
                    onSelect={() => groupSelect('LESSERAFIM')}> 
                    LE SSERAFIM 
                </TopButton>
            </menu>
            <section>
                {tabContent}
            </section>
        </div>
    )
}

export default Home
